import { Component, OnInit } from '@angular/core';
import { Product } from 'src/app/common/product';
import { ProductService } from 'src/app/services/product.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit{

  products:Product[]=[];

  constructor(private productService:ProductService){

  }

  ngOnInit(): void {
    this.productService.getProducts().subscribe(
      data=> this.products=data
    );
  }

}
<app-header-admin></app-header-admin>

<div class="container">
    <header class="jumbotron my-4">
        <h1 class="display-3">Bienvenido a Spring Ecommerce</h1>
        <p class="lead">Tu tienda de productos en linea</p>
    </header>
    <div class="main-content">
        <div class="section-content section-content-p30">
            <div class="container-fluid">
                <div class="row">
                    <div  *ngFor="let p of products" class="col-md-3">
                        <div class="product-box">
                            <td><img src="{{p.urlImage}}" style="max-width: 80px; height: 100px;"></td>
                            <h2 class="card-text">{{p.name}}</h2>
                            <div class="price">{{p.price}}</div>
                            <div class="product-show">
                                <a href="" class="primary-btn">Ver Producto</a>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
